scriptItem.scriptCode:
import hou
from utils.open_vex_in_vsc import openVexInVSC

parms = kwargs['parms']

for parm in parms:
    # just save snippet
    if parm.name() == 'snippet' or parm.name() == 'python':
        code = parm.unexpandedString()
        if len(code) == 0:
            continue

        openVexInVSC(code)
